const element=document.querySelector("#cursor"),targetElement=document.querySelectorAll(".target-element");class Cursor{constructor(e){this.el=e,this.bind()}bind(){window.addEventListener("mousemove",this.move.bind(this),!1)}move(e){const t={left:e.clientX,top:e.clientY};TweenMax.to(this.el,.3,{left:t.left,top:t.top})}}const cursor=new Cursor(element);targetElement.forEach((e=>{e.addEventListener("mouseover",(function(){element.classList.remove("not-active"),element.classList.add("cursor--expanded")})),e.addEventListener("mouseleave",(function(){element.classList.remove("cursor--expanded"),element.classList.add("not-active")}))}));